mapper.select:
SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
UPDATE INVENTORY 
SET QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}

UPDATE ITEM
SET TITLE = #{title}, DESCRIPTION = #{description}, IMG = #{img}, QUANTITY = #{quantity}, SHIPPINGFEE = #{shippingFee}
WHERE ITEMID = #{itemId}

UPDATE ITEM
SET VIEWS = VIEWS + 1
WHERE ITEMID = #{ITEMID}
